import React from "react";
import yelp from "./Yelp";
const people = ["chime", "paul", "hillary"];
const Input = props => (
  <div>
    <form onSubmit={props.handleSubmit}>
      <input
        type="text"
        value={props.value}
        placeholder="search"
        onChange={props.handleChange}
      />
      <button>click</button>
    </form>
  </div>
);

class Parent extends React.Component {
  state = {
    value: "",
    apiCall: yelp
  };

  handleChange = e => {
    this.setState({ value: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    console.log(yelp.searchYelp("news", "USA"));
    if (people.includes(this.state.value)) {
      alert("yes");
      return false;
    } else {
      alert("no");
      return false;
    }
  };

  render() {
    return (
      <div>
        <Input
          value={this.state.value}
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
        />
      </div>
    );
  }
}
export default Parent;
