                                                                              location
                                                                            }
                                                                            list={
                                                                              "location"
                                                                            }
                                                                          />
                                                                          <CustomInput
                                                                            onChange={
                                                                              handleChange
                                                                            }
                                                                            defaultValue={
                                                                              "best_match"
                                                                            }
                                                                            name={
                                                                              "sort_by"
                                                                            }
                                                                            value={
                                                                              sort_by
                                                                            }
                                                                            textTransform={
                                                                              "lowerCase"
                                                                            }
                                                                          />
