import React from "react";

const people = ["chime", "paul", "hillary"];
class Input extends React.Component {
  state = {
    value: ""
  };

  handleChange = e => {
    this.setState({ value: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();

    if (people.includes(this.state.value)) {
      alert("yes");
      return false;
    } else {
      alert("no");
      return false;
    }
  };
  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input
          type="text"
          value={this.state.value}
          placeholder="search"
          onChange={this.handleChange}
        />
        <button>click</button>
      </form>
    );
  }
}
export default Input;
